var cartApp = angular.module("cartApp", []); 
cartApp.controller("cartCtrl", function ($scope) {
    $scope.products = data.products;
    $scope.extraProducts = data.extraProducts;
    $scope.newProduct = data.newProduct;
    $scope.id = data.id;
    $scope.addNewProduct = function() {
        $scope.products.push({
            name: $scope.newProduct.name, 
            category: $scope.newProduct.category, 
            description: $scope.newProduct.description,      
            price: $scope.newProduct.price
        });
        $scope.newProduct.name = "";
        $scope.newProduct.category = "";
        $scope.newProduct.description = "";
        $scope.newProduct.price = "";
    };
});