var cartApp = angular.module("cartApp", []); 
cartApp.controller("cartCtrl", function ($scope) {
    $scope.products = data.products;
    $scope.addNewProduct = function() {
        $scope.products.push({
            name: $scope.newProductName, 
            category: $scope.newProductCategory, 
            description: $scope.newProductDescription,      
            price: $scope.newProductPrice
        });
        $scope.newProductName = '';
        $scope.newProductCategory = '';
        $scope.newProductDescription = '';
        $scope.newProductPrice = '';
    };
});