var vm = new Vue({
    el: '#cartApp',
    data: data,
    computed: {
        inCart: function() {
            return this.products.filter(function(product) {
                return product.selected == true;
            })
        },
        outCart: function() {
            return this.products.filter(function(product) {
                return product.selected == false;
            })
        }
    },
})