var vm = new Vue({
    el: '#cartApp',
    data: data,
    computed: {
        inCart: function() {
            return this.products.filter(function(product) {
                return product.selectedQuantity;
            })
        },
        priceCart: function() {
            var total = 0;
            for(var i = 0; i < this.inCart.length; i++){
                var product = this.inCart[i];
                total += (product.price * product.selectedQuantity);
            }
        return total;
        },
        outCart: function() {
            return this.products.filter(function(product) {
                return product.selectedQuantity == null;
            })
        }
    },
})