var vm = new Vue({
    el: '#cartApp',
    data: data,
    methods: {
        addNewProduct: function () {
            this.products.push({
                name: this.newProduct.name,
                category: this.newProduct.category,
                description: this.newProduct.description,
                price: this.newProduct.price,
                amount: 1
            })
            this.newProduct = ""
        },
    }
})